# test_parameterized.pyimport pytestfrom wallet import Wallet@pytest.fixturedef empty_wallet():    '''Returns a Wallet instance with a zero balance'''    return Wallet()@pytest.mark.parametrize("deposited, withdrawn, balance", [    (30, 10, 20),   # combination of values for testing    (20, 2, 18),    (0, 10, -10)])def test_transactions(empty_wallet, deposited, withdrawn, balance):    empty_wallet.deposit(deposited)    empty_wallet.withdraw(withdrawn)    assert empty_wallet.balance == balance